import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import CounterArtifact from '../../artifacts/contracts/Counter.sol/Counter.json';

const contractAddress = "YOUR_DEPLOYED_CONTRACT_ADDRESS";

function App() {
  const [count, setCount] = useState<number>(0);
  const [contract, setContract] = useState<ethers.Contract | null>(null);

  useEffect(() => {
    const init = async () => {
      if (typeof window.ethereum !== 'undefined') {
        await window.ethereum.request({ method: 'eth_requestAccounts' });
        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const contractInstance = new ethers.Contract(contractAddress, CounterArtifact.abi, signer);
        setContract(contractInstance);
        updateCount(contractInstance);
      }
    };
    init();
  }, []);

  const updateCount = async (contractInstance: ethers.Contract) => {
    const currentCount = await contractInstance.getCount();
    setCount(Number(currentCount));
  };

  const handleIncrement = async () => {
    if (contract) {
      await contract.increment();
      updateCount(contract);
    }
  };

  const handleDecrement = async () => {
    if (contract) {
      await contract.decrement();
      updateCount(contract);
    }
  };

  return (
    <div className="App">
      <h1>Counter: {count}</h1>
      <button onClick={handleIncrement}>Increment</button>
      <button onClick={handleDecrement}>Decrement</button>
    </div>
  );
}

export default App;
